package com.pic.Generic;

public class People
{
	private int id;
	private String name;

	public People()
	{
		// TODO Auto-generated constructor stub
	}
	
	public People(int id)
	{
		this.id=id;
	}

	public int getId()
	{
		return id;
	}

	public void setId(int id)
	{
		this.id = id;
	}

	public String getName()
	{
		return name;
	}

	public void setName(String name)
	{
		this.name = name;
	}
	
	

}
